mapper.select:
select
  id, problemtype, nums, classification, Subcategory, outline, describes, propose, grade, remarks
from pinpoint


        and id = #{id}


        and problemtype = #{problemtype}


        and nums = #{nums}


        and classification = #{classification}


        and Subcategory = #{subcategory}


        and outline = #{outline}


        and describes = #{describes}


        and propose = #{propose}


        and grade = #{grade}


        and remarks = #{remarks}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from pinpoint


        and id = #{id}


        and problemtype = #{problemtype}


        and nums = #{nums}


        and classification = #{classification}


        and Subcategory = #{subcategory}


        and outline = #{outline}


        and describes = #{describes}


        and propose = #{propose}


        and grade = #{grade}


        and remarks = #{remarks}
